import"./hoisted.fDDyMQpn.js";const l=document.getElementById("search"),r=document.getElementById("postList"),n=Array.from(r.children);l.addEventListener("input",t=>{const s=t.target.value.toLowerCase();n.forEach(e=>{const a=e.querySelector("a").textContent.toLowerCase(),c=e.querySelector("p").textContent.toLowerCase();a.includes(s)||c.includes(s)?e.style.display="":e.style.display="none"})});const d=document.getElementById("darkModeToggle"),o=document.documentElement;d.addEventListener("click",()=>{o.classList.toggle("dark");const t=o.classList.contains("dark");localStorage.setItem("darkMode",t?"true":"false")});const i=localStorage.getItem("darkMode");i==="true"&&o.classList.add("dark");console.log("All posts (client-side):",n);
